import { MappingLayer } from '../../adaptor/layer';
function getRampColors(style) {
  const {
    colorsRamp
  } = style;
  const rampColors = {
    colors: [],
    positions: []
  };
  colorsRamp.forEach(({
    color,
    position
  }) => {
    rampColors.colors.push(color);
    rampColors.positions.push(position);
  });
  return rampColors;
}
export function mappingLayer(layer, options) {
  const {
    shape,
    size,
    style,
    state
  } = options;
  // mapping shape
