import { deepMix, isString, substitute } from '@antv/util';
import { modifyCSS } from '@antv/dom-util';
import DomStyles from './theme';
import { CONTAINER_CLASS, CONTAINER_TPL, ITEM_TPL, LIST_CLASS, TITLE_CLASS } from './constants';
import { Component } from '../../core/component';
import { clearDom } from '../../utils/dom';
export class CategoryLegend extends Component {
  /**
   * 获取默认配置
   */
  getDefaultOptions() {
    return deepMix({}, super.getDefaultOptions(), {
      id: 'l7plot-category-legend',
      name: 'l7plot-category-legend',
      title: '',
      items: [],
      containerTpl: CONTAINER_TPL,
      itemTpl: ITEM_TPL,
      domStyles: DomStyles,
      className: CONTAINER_CLASS
    });
  }
  /**
   * 初始化 container
   */
  initContainer() {
    const {
      customContent
    } = this.options;
    if (customContent) {
      const container = this.getHtmlContentNode(customContent);
      const parentContainer = this.getParentContainer();
      if (parentContainer) {
        parentContainer.appendChild(container);
      }
      return container;
    } else {
      return super.initContainer();
    }
  }
  /**
   * 初始化 DOM
   */
  initDom() {
    this.cacheDoms();
  }
  /**
   * 初始化事件
   */
  initEvent() {
    //
  }
  /**
   * 清理事件
   */
  removeEvent() {
    //
  }
  /**
   * 缓存 DOM
   */
  cacheDoms() {
    const container = this.container;
    const titleDom = container.getElementsByClassName(TITLE_CLASS)[0];
    const listDom = container.getElementsByClassName(LIST_CLASS)[0];
    this.titleDom = titleDom;
    this.listDom = listDom;
  }
  /**
   * 绘制组件
   */
  render() {
    if (this.options.customContent) {
      this.renderCustomContent(this.options.customContent);
    } else {
      this.resetTitle();
      this.renderItems();
    }
  }
  /**
   * 显示
   */
  show() {
    const container = this.container;
    if (!container || this.destroyed) return;
    modifyCSS(container, {
      visibility: 'visible'
    });
  }
  /**
   * 隐藏
   */
  hide() {
    const container = this.container;
    if (!container || this.destroyed) return;
    modifyCSS(container, {
      visibility: 'hidden'
    });
  }
  /**
   * 更新
   */
  updateInner(options) {
    if (this.options.customContent) {
      this.renderCustomContent(this.options.customContent);
    } else {
      if (options.title) {
        this.resetTitle();
      }
      if (options.items) {
        this.renderItems();
      }
    }
    super.updateInner(options);
  }
  /**
   * 根据 customContent 渲染 DOM
   */
  renderCustomContent(customContent) {
    const parentContainer = this.container.parentNode;
    const node = this.getHtmlContentNode(customContent);
    const curContainer = this.container;
    if (parentContainer) {
      parentContainer.replaceChild(node, curContainer);
    }
    this.container = node;
    this.applyStyles();
  }
  /**
   * 生成自定义内容 DOM
   */
  getHtmlContentNode(customContent) {
    let node;
    const element = customContent(this.options.title || '', this.options.items);
    if (isString(element)) {
      node = this.createDom(element);
    } else {
      node = element;
    }
    return node;
  }
  /**
   * 重置 title
   */
  resetTitle() {
    const title = this.options.title;
    if (title) {
      this.showTitle();
      this.setTitle(title);
    } else {
      this.hideTitle();
    }
  }
  /**
   * 显示 title
   */
  showTitle() {
    const titleDom = this.titleDom;
    if (titleDom) {
      modifyCSS(titleDom, {
        display: 'block'
      });
    }
  }
  /**
   * 隐藏 title
   */
  hideTitle() {
    const titleDom = this.titleDom;
    if (titleDom) {
      modifyCSS(titleDom, {
        display: 'none'
      });
    }
  }
  /**
   * 设置 title 内容
   */
  setTitle(content) {
    const titleDom = this.titleDom;
    if (titleDom) {
      titleDom.innerHTML = content;
    }
  }
  /**
   * 渲染每项 item
   */
  renderItems() {
    this.clearItemDoms();
    const items = this.options.items.length > 30 ? this.options.items.slice(0, 30) : this.options.items;
    const itemTpl = this.options.itemTpl || ITEM_TPL;
    const listDom = this.listDom;
    if (listDom) {
      items.forEach(item => {
        const isEmpty = item.value === '';
        const value = isEmpty ? '—' : Array.isArray(item.value) ? item.value.join(' - ') : item.value;
        const substituteObj = Object.assign(Object.assign({}, item), {
          value
        });
        const domStr = substitute(itemTpl, substituteObj);
        const itemDom = this.createDom(domStr);
        listDom.appendChild(itemDom);
      });
      this.applyChildrenStyles(listDom, this.options.domStyles);
    }
  }
  /**
   * 清空 list DOM 下的 DOM 元素
   */
  clearItemDoms() {
    if (this.listDom) {
      clearDom(this.listDom);
    }
  }
  /**
   * 清空所有
   */
  clear() {
    this.setTitle('');
    this.clearItemDoms();
  }
}
