import { pick } from '@antv/util';
import { Plot } from '../../core/plot';
import { DEFAULT_OPTIONS } from './constants';
import { DotLayer } from '../../layers/dot-layer';
import { LayerGroup } from '../../core/layer/layer-group';
export class Dot extends Plot {
  constructor() {
    super(...arguments);
    /**
     * 图表类型
     */
    this.type = Plot.PlotType.Dot;
  }
  /**
   * 获取默认配置
   */
  getDefaultOptions() {
    return Dot.DefaultOptions;
  }
  /**
   * 创建图层
   */
  createLayers(source) {
    this.dotLayer = new DotLayer(Object.assign({
      source
    }, pick(this.options, DotLayer.LayerOptionsKeys)));
    const layerGroup = new LayerGroup([this.dotLayer]);
    if (this.options.label) {
      this.labelLayer = this.createLabelLayer(source, this.options.label, this.options);
      layerGroup.addLayer(this.labelLayer);
    }
    return layerGroup;
  }
  /**
   * 更新图层
   */
  updateLayers(options) {
    const dotLayerConfig = pick(options, DotLayer.LayerOptionsKeys);
    this.dotLayer.update(dotLayerConfig);
    this.updateLabelLayer(this.source, options.label, this.options, this.labelLayer);
  }
  /**
   * 实现 legend 配置项
   */
  getLegendOptions() {
    const colorLegendItems = this.dotLayer.getColorLegendItems();
    if (colorLegendItems.length !== 0) {
      return {
        type: 'category',
        items: colorLegendItems
      };
    }
    return {};
  }
}
/**
 * 默认配置项
 */
Dot.DefaultOptions = DEFAULT_OPTIONS;
