/**
 * 缓存的行政数据
 */
export const AREA_CACHE = new Map();
export function registerCacheArea(key, area) {
  AREA_CACHE.set(key, area);
}
export function registerCacheAreas(areaList) {
  areaList.forEach(item => {
    AREA_CACHE.set(item.key, item.area);
  });
}
export function getCacheArea(key) {
  return AREA_CACHE.get(key);
}
export function hasCacheArea(key) {
  return AREA_CACHE.has(key);
}
export function unregisterCacheArea(key) {
  return AREA_CACHE.delete(key);
}
